export const uploadFile = async (file) => {
  try {
    const token = localStorage.getItem('access_token');
    const formData = new FormData();
    formData.append('file', file);
    formData.append('share_type', 'private');
    formData.append('share_expiry', new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString());
    formData.append('file_type', file.type);
    formData.append('shared_to', JSON.stringify([]));

    const response = await fetch('http://localhost:8000/api/shared-files/', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`
      },
      body: formData
    });
    return response.data;
  } catch (error) {
    console.log(error);
    console.log(error.response);
    return { error: error.response?.data?.detail || 'File upload failed' };
  }
};

export const getFiles = async () => {
  try {
    const token = localStorage.getItem('access_token');
    const response = await fetch('http://localhost:8000/api/shared-files/', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      }
    });
    const data = await response.json();
    return data;
  } catch (error) {
    console.log(error);
    return { error: error.response?.data?.detail || 'Failed to get files' };
  }
}
